import React from 'react';
import { getHeaders } from './utils.js';


class Posts extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            posts: {}
        }
        console.log('Posts component created');
        this.getPosts();
    }

    getPosts() {
        fetch('/api/posts', {
            headers: getHeaders()
        }).then(response => response.json())
        .then(data => {
            console.log(data);
            // when you update the state, you trigger the refresh
            this.setState({
                posts: data
            });
        })
    }

    // componentDidMount() {
    //     //fetch posts
    // }

    render () {
        return <div id="posts">
                        Posts
                        {/* Posts */}
                    </div>
    }
}

export default Posts;
