import React from 'react';
import {getHeaders} from './utils.js';

class BookmarkButton extends React.Component { 

    constructor(props) {
        super(props);
        // data we need: 
        // 1. whether current post is bookmarked
        //2. current post id (which post)

        this.toggleBookmark = this.toggleBookmark.bind(this);
        this.bookmark = this.bookmark.bind(this);
        this.unbookmark = this.unbookmark.bind(this);
    }

    toggleBookmark (ev) {
        console.log('likeUnlike');
        if(this.props.bookmarkId) {
            this.unbookmark();
        } else {
            this.bookmark();
        }
    }

    bookmark () {
        console.log('bookmark');
        const headers = getHeaders();
        headers['Content-Type']= 'application/json';
        fetch('/api/bookmarks', {
            headers: headers,
            method: 'POST',
            body: JSON.stringify({post_id: this.props.postId})
        })
        .then(response => response.json())
        .then(data => {
            console.log(data); 
            this.props.requeryPost();
        })
    }

    unbookmark () {
        console.log('unbookmark');
        fetch('/api/bookmarks/' + this.props.bookmarkId, {
            headers: getHeaders(),
            method: 'DELETE',
            body: JSON.stringify({post_id: this.props.postId})
        })
        .then(response => response.json())
        .then(data => {
            console.log(data);
            this.props.requeryPost();
        })

    }

    render () {
        const bookmarkId = this.props.bookmarkId;
        return (
            <button 
                className='bookmark'
                role="switch"
                onClick={ this.toggleBookmark }
                aria-label= {bookmarkId ? "unbookmark" : "bookmark"}
                aria-checked={ bookmarkId ? true : false}>
                <i className={ bookmarkId ? 'fas fa-bookmark' : 'far fa-bookmark'}></i>
            </button>
        ); 
    }
}

export default BookmarkButton;
