import React from 'react';

class Stories extends React.Component {  

    constructor(props) {
        super(props);
        this.state = {
            stories: []
        }
        // constructor logic
        console.log('Stories component created');
        this.getStories();
    }

    componentDidMount() {
        // fetch posts
        console.log('Stories component mounted');
    }

    getStories(){
        fetch('/api/stories')
        .then(response => response.json())
        .then(data => {
            this.setState({
                stories: data
            })
        })
    }

    render () {
        return (
            <div className="stories">
                {
                    this.state.stories.map(story => {
                        <section class="individual-story">  
                            <img src={story.user.profile_url} width="75" height="75" alt= {story.user.username + "'s profile picture"} />
                            <p>{story.user.username}</p>
                        </section>
                    })
                }
            </div>
        )
    }
}

export default Stories;
